con:config.script:
import com.eviware.soapui.support.XmlHolder
context.setProperty("name", "automated")
addRequest = context.expand('${TSP_addEmployee#request}') addRequestXml = new XmlHolder(addRequest) desiredValue = addRequestXml.getNodeValue("//typ:name")
//pass to get Employee getRequest = context.expand('${TSP_getEmployee#request}') getRequestXML = new XmlHolder(getRequest) getRequestXML.setNodeValue("//typ:employeeName" ,desiredValue )
finalXml = getRequestXML.getXml()
//set the value in get Employee testRunner.testCase.testSteps["TSP_getEmployee"].setPropertyValue("request",finalXml) //Run add and get Employee runAddEmployee = testRunner.testCase.testSteps["TSP_addEmployee"].run(testRunner, context) runGetEmployee = testRunner.testCase.testSteps["TSP_getEmployee"].run(testRunner, context)
